<?php require_once("../includes/settings.php") ?><?phpfunction openConnection(){	global $connection;	global $sqlhost, $sqluser, $sqlpassword, $sqldb;		if (!$connection = mysql_connect($sqlhost, $sqluser, $sqlpassword))		die($_POST["prenoconnectdb"] . mysql_error() . $_POST["postnoconnectdb"]);		if (!mysql_select_db($sqldb, $connection))		die($_POST["prenoselectdb"] . mysql_error() . $_POST["postnoselectdb"]);}function clearTable($connection, $tablename){	$query = "TRUNCATE `{$tablename}`";	$result = mysql_query($query, $connection);	if (!$result)		die($_POST["preinvalidquery"] . mysql_error() . $_POST["postinvalidquery"]);}?>